mapper.update:
update goods_reply_table set gcomment = #{gcomment},
gscore = #{gscore}, gupdateDate = sysdate where gno = #{gno} and
userNo = #{userNo}

mapper.select:

SELECT * FROM (
    SELECT ROWNUM AS rnum, a.* 
    FROM ( 
        SELECT * FROM goods_reply_table 
        WHERE gno = #{gno} AND userNo != #{userNo} 
        ORDER BY gRegDate DESC
    ) a 
    WHERE ROWNUM <= (#{offset} + #{size})
) 
WHERE rnum > #{offset}

SELECT COUNT(*) FROM
goods_reply_table
WHERE gno = #{gno} AND userNo != #{userNo}


SELECT * FROM (
    SELECT a.*, ROWNUM AS row_num
    FROM (
        SELECT * FROM goods_reply_table 
        WHERE userNo = #{userNo}  
        ORDER BY gno DESC
    ) a
    WHERE ROWNUM <= #{endRow}
)
WHERE row_num > #{startRow}
