mapper.select:
SELECT COUNT(*)
FROM pop_reply_tbl
WHERE psNo = #{psNo} AND userNo = #{userNo}


SELECT * FROM (
    SELECT ROWNUM rnum, a.* FROM (
        SELECT * FROM pop_reply_tbl 
        WHERE psNo = #{psNo} AND userNo != #{userNo}
        ORDER BY psRegDate DESC
    ) a
    WHERE ROWNUM <= #{pageNum} * #{amount}
)
WHERE rnum > (#{pageNum} - 1) * #{amount}


SELECT * FROM (
    SELECT a.*, ROWNUM AS row_num
    FROM (
        SELECT * 
        FROM pop_reply_tbl
        WHERE userNo = #{userNo} 
        ORDER BY psRegDate DESC
    ) a
    WHERE ROWNUM <= #{endRow}  
)
WHERE row_num > #{startRow}


SELECT COUNT(*) 
FROM pop_reply_tbl
WHERE userNo = #{userNo}
