mapper.select:

SELECT * FROM (
   SELECT a.*, ROWNUM AS row_num
   FROM (
      SELECT * FROM notice_tbl ORDER BY noticeRegDate DESC
   ) a
   WHERE ROWNUM <= #{endRow}
)
WHERE row_num > #{startRow}


SELECT * FROM (
   SELECT a.*, ROWNUM AS row_num
   FROM (
      SELECT * FROM qna_tbl where userNo = #{userNo} order by qnaRegDate DESC
   ) a
   WHERE ROWNUM <= #{endRow}
)
WHERE row_num > #{startRow}

mapper.update:
